$(window).resize(function(){
	location.reload();
});

$(document).ready(function() {

	// lazy load
	$('[data-src]').each(function(){
		var img = $(this).data('src');
		$(this).attr('src', img);	
	});


	// para variar 
	$fullPage = $('#fullpage--vbd'),
	$mobile = $('.mobile'),
	$pageHome = $('.page-home'),
	$pageApp = $('.page-oapp'),
	$pageVouagora = $('.page-vouagora'),
	$nav = $('.nav-site'),
	$slideVouagora = $('.mobile-sections_vouagora'),
	$nextPage = $('.next-page');

	// width mobile, prevent bug
	$mobile.width($mobile.width()).height($mobile.height());

	// btn next page
	$nextPage.addClass('load');
	$nextPage.click(function(){
		$.fn.fullpage.moveSectionDown();
	});

	// full page
	$fullPage.fullpage({
		css3: true,
		scrollBar : true,
		onLeave: function(index, nextIndex, direction){
			if (nextIndex > 1) {
				$nav.addClass('fixed');
				$('.page-home_action').removeClass('form-active');
			};

			if (nextIndex == 1) {
				$mobile.removeClass('pageApp-move pageApp-mobile');
				$nav.removeClass('fixed');
			};

			if (nextIndex == 2) {
				$mobile.addClass('pageApp-mobile');
				$mobile.removeClass('pageVouagora');
				var dobra = true;
				if (dobra) {
					ga('send', 'event', 'pagina', 'desceu-dobra-pagina');
					dobra = false;
				};
			};

			if (nextIndex > 3) {
				$mobile.addClass('pageVouagora');
			}

			if (nextIndex == 4) {
				$('.page-free .fp-tableCell').animate({
					scrollTop: 10
				}, '400');
			}
		},

		afterLoad : function(anchorLink, index){

			if (index == 1) {
				$('.view').removeClass('view');
				$.fn.fullpage.setAutoScrolling(true);
			};

			if(index >= 2) {
				$mobile.addClass('pageApp-move');
				$pageApp.addClass('view');
				$.fn.fullpage.setAutoScrolling(true);
			} else {
			}
			if(index >= 3) {
				$mobile.addClass('pageVouagora');
				$pageVouagora.addClass('view');
				$.fn.fullpage.setAutoScrolling(true);
				$.getScript('src/build/social.js');
			} else if (index < 3) {
				$mobile.removeClass('pageVouagora');
			}
			if(index == 4) {
				$nav.addClass('gray');
				$nextPage.addClass('none');
				$.fn.fullpage.setAutoScrolling(false);
			} else {
				$nextPage.removeClass('none');
				$nav.removeClass('gray');
			}
		}
	});


	/*=======================================
	=            slide vou agora            =
	=======================================*/

	var sw = $('.mobile-sections').width();
	var sh = $('.mobile-sections').height();
	$('.mobile-sections_item').width(sw).height(sh);

	var owl = $slideVouagora.data('owlCarousel');
	
	$slideVouagora.owlCarousel({
		singleItem : true,
		slideSpeed : 600,
		navigation : true,
		pagination : false,
		afterInit  : function(){
			$slideVouagora.removeAttr('style');
		},
		afterAction : function() {
			var current = this.currentItem+1;
			$('.dots a:nth-child('+current+')').addClass('active').siblings().removeClass('active');
		}
	});

	$('.arrow.next').on('click',function(){
		$slideVouagora.trigger('owl.next');
	});
	$('.arrow.prev').on('click',function(){
		$slideVouagora.trigger('owl.prev');
	});

	$('.dots').on('click', 'a', function(){
		var target = $(this).index();

		console.log(target)

		$slideVouagora.trigger('owl.goTo', target);
		$(this).addClass('active').siblings().removeClass('active');
	});
	
	/*-----  End of slide vou agora  ------*/
	

	


	// // return fullpage
	// var fullpageReturn = (function() {
	// 	'use strict';

	// 	var timeWindow = 200; // time in ms
	// 	var timeout;

	// 	var fullpageReturn = function() {


	// 	};

	// 	return function() {
	// 		var context = this;
	// 		var args = arguments;
	// 		clearTimeout(timeout);
	// 		timeout = setTimeout(function() {
	// 			fullpageReturn.apply(context, args);
	// 		}, timeWindow);
	// 	};
	// }());

	$('.page-free .fp-tableCell').on('scroll',function(){
		if($('.page-free .fp-tableCell').scrollTop() == 0) {
			$.fn.fullpage.setAutoScrolling(true);
		}
	});






	/*============================
	=            menu            =
	============================*/

		$('.logo-site, .download').on('click', function(){
			$.fn.fullpage.moveTo(1);
		});


		$nav.on('click', 'a', function(){
			var target = $(this).index()+2;

			// free page
			if (target > 3 && target < 8) {
				$.fn.fullpage.moveTo(4);

				var section = $(this).data('target');
				console.log($(section).position().top - 20);

				$('.page-free .fp-tableCell').animate({
					scrollTop: $(section).position().top - 20
				}, '3000');			
			} else {
				// normal
				$.fn.fullpage.moveTo(target);
			}
		});
	
	
	/*-----  End of menu  ------*/
	


});



